import React from 'react';
import ResponsiveDrawer from "../components/Navigation";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { useAdmin } from "../context/RoleContext";
import { useStyles } from "../components/Styles";

function AdminRoles(props) {
  const accounts = props.accounts;
  const supplyChainContract = props.supplyChainContract;
  const { roles, setRoles } = useRole();

  const classes = useStyles();
  const [manufacturerAdmin, setManufacturerAdmin] = React.useState("");
  const [thirdPartyAdmin, setThirdPartyAdmin] = React.useState("");
  const [deliveryAdmin, setDeliveryAdmin] = React.useState("");
  const [customerAdmin, setCustomerAdmin] = React.useState("");
  const navItem = [];

  const handleAddManufacturerAdmin = async () => {
    await setAdmins({
      ...admins, 
      manufacturer : manufacturerRole
    })

    localStorage.setItem("mAdmin", manufacturerAdmin);
    await supplyChainContract.methods.addManufacturerAdmin(manufacturerAdmin).send({ from: accounts[0], gas:100000 })
    .then(console.log);



    setManufacturerAdmin("");
  }

  const handleAddThirdPartyAdmin = async () => {
    await setAdmins({
      ...admins, 
      thirdparty : thirdPartyAdmin
    })

    localStorage.setItem("tpAdmin", thirdPartyAdmin);
    await supplyChainContract.methods.addThirdPartyAdmin(thirdPartyAdmin).send({ from: accounts[0], gas:100000 })
    .then(console.log);



    setThirdPartyAdmin("");
  }

  const handleAddDeliveryAdmin = async () => {
    await setAdmins({
      ...admins, 
      delivery : deliveryAdmin
  })

   localStorage.setItem("dAdmin", deliveryAdmin);
    await supplyChainContract.methods.addDeliveryAdmin(deliveryAdmin).send({ from: accounts[0], gas:100000 })
    .then(console.log);



    setDeliveryAdmin("");
  }

  const handleAddCustomerAdmin = async () => {
    await setAdmins({
      ...admins, 
    customer : customerAdmin
  })

   localStorage.setItem("cAdmin", customerAdmin);
    await supplyChainContract.methods.addCustomerAdmin(customerAdmin).send({ from: accounts[0], gas:100000 })
    .then(console.log);



    setCustomerAdmin("");
  }


  return (
    <div>
      <ResponsiveDrawer navItems={navItem}>
      <div className={classes.FormWrap}>
      <h1 className={classes.pageHeading}>Add Admins</h1>
      {console.log(admins)}

      <form className={classes.root} noValidate autoComplete="off">
        <div className={classes.AdminForm} >
          <TextField
            id="manufacturerAdmin"
            label="Enter Manufacturer Ethereum Address"
            variant="outlined"
            value={manufacturerAdmin}
            onChange={(e) => setManufacturerAdmin(e.target.value)}
            style={{width:"70%"}}
          />
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddManufacturerAdmin}
            style={{width:"20%", marginLeft:"30px"}}
          >
            Save Manufacturer
          </Button>
        </div>
      </form>

      <form className={classes.root} noValidate autoComplete="off">
        <div className={classes.AdminForm} >
          <TextField
            id="thirdPartyAdmin"
            label="Enter Third Party Ethereum Address "
            variant="outlined"
            value={thirdPartyAdmin}
            onChange={(e) => setThirdPartyAdmin(e.target.value)}
            style={{width:"70%"}}
          />
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddThirdPartyAdmin}
            style={{width:"20%", marginLeft:"30px"}}
          >
            Save Third Party
          </Button>
        </div>
      </form>

      <form className={classes.root} noValidate autoComplete="off">
        <div className={classes.AdminForm} >
          <TextField
            id="deliveryAdmin"
            label="Enter Delivery Ethereum Address"
            variant="outlined"
            value={deliveryAdmin}
            onChange={(e) => setDeliveryAdmin(e.target.value)}
            style={{width:"70%"}}
          />
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddDeliveryAdmin}
            style={{width:"20%", marginLeft:"30px"}}
          >
            Save delivery
          </Button>
        </div>
      </form>

      <form className={classes.root} noValidate autoComplete="off">
        <div className={classes.AdminForm} >
          <TextField
            id="customerAdmin"
            label=" Enter Customer Ethereum Address"
            variant="outlined"
            value={customerRole}
            onChange={(e) => setCustomerAdmin(e.target.value)}
            style={{width:"70%"}}
          />
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddCustomerAdmin}
            style={{width:"20%", marginLeft:"30px"}}
          >
            Save customer
          </Button>
        </div>
      </form>
      </div>
      </ResponsiveDrawer>
    </div>
  );
}

export default AdminRoles;
