import React from "react";
import Navbar from "../../components/Navbar";
import Button from "@material-ui/core/Button";
import { useRole } from "../../context/RoleDataContext";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import TablePagination from "@material-ui/core/TablePagination";
import { useStyles } from "../../components/Styles";
import ProductModal from "../../components/Modal";
import clsx from "clsx";
import Loader from "../../components/Loader";

export default function ShipThirdParty(props) {
  const classes = useStyles();
  const supplyChainContract = props.supplyChainContract;
  const { roles } = useRole();
  const [count, setCount] = React.useState(0);
  const [allSoldProducts, setAllSoldProducts] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const navItem = [
    ["Buy Product", "/ThirdParty/allProducts"],
    ["Receive Product", "/ThirdParty/receive"],
    ["Shipping Products", "/ThirdParty/ship"],
  ];
  const [alertText, setalertText] = React.useState("");
  React.useEffect(() => {
    (async () => {
      setLoading(true);
      const cnt = await supplyChainContract.methods.fetchItemCount().call();
      setCount(cnt);

    })();

    (async () => {
      const arr = [];
      for (var i = 1; i < count; i++) {
        const prodState = await supplyChainContract.methods
          .fetchItemState(i)
          .call();

        if (prodState === "4") {
          const prodData = [];
          const a = await supplyChainContract.methods
            .fetchItemPart1(i, "product", 0)
            .call();
          const b = await supplyChainContract.methods
            .fetchItemPart2(i, "product", 0)
            .call();
          const c = await supplyChainContract.methods
            .fetchItemPart3(i, "product", 0)
            .call();
          prodData.push(a);
          prodData.push(b);
          prodData.push(c);
          arr.push(prodData);
        }
      }
      setAllSoldProducts(arr);
      setLoading(false);
    })();
  }, [count]);

  const handleSetTxhash = async (id, hash) => {
    await supplyChainContract.methods
      .setTransactionHash(id, hash)
      .send({ from: roles.manufacturer, gas: 900000 });
  };

  const handleShipButton = async (id) => {
    try{
      await supplyChainContract.methods
      .shipByThirdParty(id)
      .send({ from: roles.thirdparty, gas: 1000000 })
      .on("transactionHash", function (hash) {
        handleSetTxhash(id, hash);
      });
     setCount(0);
    }catch{
      setalertText("You are not the owner of the Product")
    }

  };
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const [open, setOpen] = React.useState(false);
  const [modalData, setModalData] = React.useState([]);

  const handleClose = () => setOpen(false);

  const handleClick = async (prod) => {
    await setModalData(prod);
    setOpen(true);
  };

  return (
    <>
      <div classname={classes.pageWrap}>
        <Navbar titlePage={"Third Party"} navItems={navItem}>
          {loading ? (
            <Loader />
          ) : (
            <>
              <ProductModal
                prod={modalData}
                open={open}
                handleClose={handleClose}
              />
              <h1 className={classes.pageHeading}>Products To be Shipped</h1>
              <h3 className={classes.tableCount}>
                Total Products : {allSoldProducts.length}
              </h3>

              <div>
              <p><b style={{ color: "red" }}>{alertText.length !== 0 ? alertText : ""}</b></p>
                <Paper className={classes.TableRoot}>
                  <TableContainer className={classes.TableContainer}>
                    <Table stickyHeader aria-label="sticky table">
                      <TableHead>
                        <TableRow>
                          <TableCell className={classes.TableHead} align="left">
                            Universal ID
                          </TableCell>
                          <TableCell
                            className={classes.TableHead}
                            align="center"
                          >
                            Product Code
                          </TableCell>
                          <TableCell
                            className={classes.TableHead}
                            align="center"
                          >
                            Manufacturer
                          </TableCell>
                          <TableCell
                            className={classes.TableHead}
                            align="center"
                          >
                            Manufacture Date
                          </TableCell>
                          <TableCell
                            className={classes.TableHead}
                            align="center"
                          >
                            Product Name
                          </TableCell>
                          <TableCell
                            className={clsx(
                              classes.TableHead,
                              classes.AddressCell
                            )}
                            align="center"
                          >
                            Owner
                          </TableCell>
                          <TableCell
                            className={clsx(classes.TableHead)}
                            align="center"
                          >
                            Ship
                          </TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {allSoldProducts.length !== 0 ? (
                          allSoldProducts
                            .slice(
                              page * rowsPerPage,
                              page * rowsPerPage + rowsPerPage
                            )
                            .map((prod) => {
                              const d = new Date(parseInt(prod[1][0] * 1000));
                              return (
                                <>
                                  <TableRow
                                    hover
                                    role="checkbox"
                                    tabIndex={-1}
                                    key={prod[0][0]}
                                  >
                                    <TableCell
                                      className={classes.TableCell}
                                      component="th"
                                      align="left"
                                      scope="row"
                                      onClick={() => handleClick(prod)}
                                    >
                                      {prod[0][0]}
                                    </TableCell>
                                    <TableCell
                                      className={classes.TableCell}
                                      align="center"
                                      onClick={() => handleClick(prod)}
                                    >
                                      {prod[1][2]}
                                    </TableCell>
                                    <TableCell
                                      className={classes.TableCell}
                                      align="center"
                                      onClick={() => handleClick(prod)}
                                    >
                                      {prod[0][4]}
                                    </TableCell>
                                    <TableCell
                                      align="center"
                                      onClick={() => handleClick(prod)}
                                    >
                                      {d.toDateString() +
                                        " " +
                                        d.toTimeString()}
                                    </TableCell>
                                    <TableCell
                                      className={classes.TableCell}
                                      align="center"
                                      onClick={() => handleClick(prod)}
                                    >
                                      {prod[1][1]}
                                    </TableCell>
                                    <TableCell
                                      className={clsx(
                                        classes.TableCell,
                                        classes.AddressCell
                                      )}
                                      align="center"
                                      onClick={() => handleClick(prod)}
                                    >
                                      {prod[0][2]}
                                    </TableCell>
                                    <TableCell
                                      className={clsx(classes.TableCell)}
                                      align="center"
                                    >
                                      <Button
                                        type="submit"
                                        variant="contained"
                                        color="primary"
                                        onClick={() =>
                                          handleShipButton(prod[0][0])
                                        }
                                      >
                                        SHIP
                                      </Button>
                                    </TableCell>
                                  </TableRow>
                                </>
                              );
                            })
                        ) : (
                          <> </>
                        )}
                      </TableBody>
                    </Table>
                  </TableContainer>
                  <TablePagination
                    rowsPerPageOptions={[10, 25, 100]}
                    component="div"
                    count={allSoldProducts.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onChangePage={handleChangePage}
                    onChangeRowsPerPage={handleChangeRowsPerPage}
                  />
                </Paper>
              </div>
            </>
          )}
        </Navbar>
      </div>
    </>
  );
}
